document.getElementById("submit-search").addEventListener("click",(function(){!async function(e){const t=await fetch("https://api.openweathermap.org/data/2.5/weather?q="+e+"&units=metric&appid=1f2ee12c8f8994c48f986ab8a9acd1d7",{mode:"cors"});if(404===t.status)console.log("error");else{const e=await t.json();console.log(e)}}(document.getElementById("location-search-input").value)}));